import React, { useEffect, useState } from "react";
import axios from "axios";
import ButtonComponent from "./components/ButtonComponent";
import "./styles.css";
import CityRow from "./components/CityRow";

export default function App() {
  const [data, setData] = useState([{}]);

  useEffect(()=>{
     getData();
  },[]);

 const getData = () => {
    return axios.get(`http://localhost:8080/Countries`)
    .then(function (res) {
      // handle success
      console.log(res.data);
      setData(res.data)
    })
    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    }); 
 }

  return (
    <div className="App">
      <div id="loading-container"></div>
      <table>
        <tr>
          <th>
            ID
          </th>
          <th>
            CITY NAME
          </th>
          <th>
            COUNTRY NAME
          </th>
          <th>
            POPULATION
          </th>
          </tr>

         {
           data.map((city)=> {
              <CityRow city={city} />
           })
         }


      </table>

      <div>
        <ButtonComponent id="SORT_BUTTON" title={`Sort by Increasing Population`} />
        <ButtonComponent title="PREV" id="PREV" text="Previous" />
        <ButtonComponent id="NEXT" title="NEXT" text="Next" />
      </div>
    </div>
  );
}
