import React, { useEffect, useState } from "react";
import axios from "axios";
import ButtonComponent from "./components/ButtonComponent";
import "./styles.css";
import CityRow from "./components/CityRow";

export default function App() {
  const [data, setData] = useState([{}]);
  const [page,setPage] = useState(1);
  const [limit,setLimit] = useState(10);



  useEffect(()=>{
     getData();
  },[page,limit]);

 const getData = () => {
    return axios.get(`http://localhost:8080/Countries?_limit=${limit}&_page=${page}`)
    .then(function (res) {
      // handle success
      console.log(res.data);
      setData(res.data)
    })
    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    }); 
 }

  return (
    <div className="App">
      <div id="loading-container"></div>
      <table>   
        <thead>
        <tr>
          <th>
            ID
          </th>
          <th>
            CITY NAME
          </th>
          <th>
            COUNTRY NAME
          </th>
          <th>
            POPULATION
          </th>
          </tr>
          </thead>     
          <tbody>         

          {
            data.map((city)=> {
              return <CityRow city={city} key={city.id}  />
            })
          } 
         </tbody>
      </table>

      <div>
        <ButtonComponent id="SORT_BUTTON" title={`Sort by Increasing Population`} />
        <ButtonComponent title="PREV" id="PREV" text="Previous"  onclick={()=>{
          setPage((prev)=> prev - 1)
          console.log(page)
        }} />
        <ButtonComponent id="NEXT" title="NEXT" text="Next" 
         onclick={()=>{
          setPage((prev)=> prev + 1)
          console.log(page)
        }} />
      </div>
    </div>
  );
}
